INSERT INTO qinggan_email (id, site_id, identifier, title, content, note) VALUES ('13', '1', 'order_user_shipped', '您的订单【{$order.sn}】已发货', '<p style="white-space: normal;"><strong>尊敬的{$fullname}，您好：</strong><br/></p><p style="white-space: normal;"><br/></p><p style="white-space: normal;">感谢您在<span style="color: rgb(0, 112, 192);">{$config.title}</span>（{$sys.url}）购物。</p><p style="white-space: normal;">您的订单<span style="color: rgb(0, 112, 192);">{$order.sn}</span>已经发货，请保持您的电话畅通，以方便快递人员能与您取得联系。</p><p style="white-space: normal;">订单信息以个人中心里的“<span style="color: rgb(0, 112, 192);"><strong>我的订单</strong></span>”信息为准，您也可以随时进入订单详细进行查看修改等操作。</p><p style="white-space: normal;"><br/></p><p style="white-space: normal;"><strong>重要说明：</strong></p><p style="white-space: normal;">本邮件仅表明销售方已收到了您提交的订单；销售方收到你的订单信息后，只有在销售方将您的订单中订购的商品从仓库实际直接向您发出时（以商品出库为标志），方视为您与销售方之间就实际直接向您发出的商品建立了合同关系；<br/>如果您在一份订单里订购了多种商品并且销售方只给您发出了部分商品时，您与销售方之间仅就实际直接向您发出的商品建立了合同关系；只有在销售方实际直接向您发出了订单中订购的其他商品时，您和销售方之间就订单中该其他已实际直接向您发出的商品建立了合同关系。<br/>您可以随时登陆您在京东注册的账户，查询您的订单状态。更多内容请见最新的京东网站用户注册协议及京东网站各类购物规则，我们建议您不时地浏览阅读。</p><p style="white-space: normal;"><br/></p><p style="white-space: normal;"><strong>账户安全提醒：</strong><br/>互联网账号存在被盗风险，为了保障您的账号及资金安全，我们提醒您访问 我的账户- &gt; 修改密码，尽量使用复杂密码，如字母+数字+特殊符号等。</p><p><br/></p>', '');
